// ==UserScript==
// @name        Tag Suggestions
// @namespace   rene_zeidler
// @description Adds suggestions when editing tags on Derpibooru
// @include     /https?://(www\.)?derpiboo.ru/.*/
// @include     /https?://(www\.)?trixiebooru.org/.*/
// @include     /https?://(www\.)?derpibooru.org/.*/
// @require     https://ajax.googleapis.com/ajax/libs/jquery/3.2.1/jquery.min.js
// @downloadURL https://gist.github.com/ReneZeidler/477fa635b2bdabd71b449df56c03b0ab/raw/Tag_Suggestions.user.js
// @installURL  https://gist.github.com/ReneZeidler/477fa635b2bdabd71b449df56c03b0ab/raw/Tag_Suggestions.user.js
// @updateURL   https://gist.github.com/ReneZeidler/477fa635b2bdabd71b449df56c03b0ab/raw/Tag_Suggestions.meta.js
// @version     1.0.1
// @grant       none
// ==/UserScript==

// source: https://www.derpibooru.org/meta/userscript-tag-suggestions/post/2388828#post_2388828

"use strict";
var config = {
	/* Configures how many tag suggestions are displayed.
	 * If there are too many rows, a scrollbar is displayed.
	 * If a tag doesn't match the relevance requirement, it isn't displayed at all.
	 * (These settings do NOT affect loading time at all)
	 */
	MAX_DISPLAY_ROWS: 4,
	MIN_RELEVANCE: 0.05,
	MAX_SUGGESTIONS: 100,

	/* Number of images to be analysed for getting suggestions.
	 * Since there is a maximum of 50 images per page, increasing this number increases
	 * loading time (and nuber of requests).
	 */
	SAMPLE_SIZE: 100,
	/* Minimum number of images with exact matching tags before switching to fuzzy search. */
	MIN_SAMPLE_SIZE: 5,

	/* Sorting parameters for the exact matching. */
	SORT: "created_at",
	ASCENDING_ORDER: false,

	/* Output debug info in console. */
	DEBUG: false
};

/////////////////////////////////////

window.addTag = function(a,b,c) {
    a.value+=', '+b;
    c.getElementsByClassName('js-taginput-show-tag_input')[0].click();
}

/////////////////////////////////////

window.tagSuggestions = {
	config: {},
	styles: '\
	.suggested_tags.updating::after {\
		background: url(data:image/gif;base64,) no-repeat center;\
		width: 100%;\
		height: 100%;\
		position: absolute;\
		display: block;\
		top: 0;\
		left: 0;\
		content: "";\
	}\
	\
	.suggested_tags.updating {\
		opacity: 0.5;\
		min-height: 70px;\
	}\
	\
	.suggested_tags {\
		margin: 5px 0 0 0;\
		background: rgba(0,0,0,0.05);\
		padding: 5px 5px 0 5px;\
		max-width: 900px;\
		min-height: 24px;\
		max-height: %MAX_HEIGHT%px;\
		overflow-y: scroll;\
		position: relative;\
	}\
	\
	.suggested_tags .tag > * {\
		cursor: pointer;\
	}\
	\
	.suggested_tags .tag:hover {\
		filter: brightness(1.05) !important;\
	}\
	\
	.suggested_tags .tag .exclude:hover {\
		color: red;\
	}\
	', //styles

	systemTags: ["safe", "suggestive", "questionable", "explicit", "semi-grimdark", "grimdark", "grotesque"],

	//State
	tags: [],
	ignoreTags: [],
	excludeTags: [],
	startFuzzy: false,

	suggestions: [],
	generatedOnce: false,

	tagsinput: null,
	tagsinputdiv: null,
	$suggestionBox: null,

	//Methods
	search: function(params, success, fail) {
		if(!params || !params.q)
			return false;

		if(!params.perpage || params.perpage < 1 || params.perpage > 50)
			params.perpage = 50;

		if(this.config.DEBUG) {
			console.log("[TagSuggestions] New search with params:");
			console.log(params);
		}

		$.ajax({
			dataType: "json",
			url: "/search.json",
			data: params,
			success: success,
			error: [fail, function(jqXHR, textStatus, errorThrown) {
					console.log("[TagSuggestions] Derpibooru API returned an error: " + textStatus + "\n" + errorThrown);
				}],
			beforeSend: function(jqXHR) {
				jqXHR.setRequestHeader("User-Agent", navigator.userAgent + " TagSuggestions/1.0");
			}
		});

		return true;
	},

	tagSearch: function(callback, page, fuzzy) {
		if(typeof callback !== "function" || this.tags.length < 1)
			return false;

		var query = "";
		if(fuzzy) {
			var systemTags = this.systemTags;
			function getModifier(tag) {
				if(systemTags.indexOf(tag) >= 0) return 3;
				if(tag.startsWith("artist:")) return 2;
				return 1;
			}

			var tag;
			for(var i = 0; i < this.tags.length - 1; i++) {
				tag = this.tags[i];
				query += tag + "^" + getModifier(tag) + " || ";
			}
			tag = this.tags[this.tags.length - 1];
			query += tag + "^" + getModifier(tag);

			if(this.excludeTags.length >= 1)
				for(var i = 0; i < this.excludeTags.length; i++)
					query += " || " + this.excludeTags[i] + "^-4";
		} else {
			query = this.tags.join(",");
			if(this.excludeTags.length >= 1)
				query = "(" + query + ") && !(" + this.excludeTags.join(" || ") + ")";
		}

		var sortingMethod = (fuzzy ? "relevance" : this.config.SORT);
		var sortingOrder  = (fuzzy || !this.config.ASCENDING_ORDER ? "desc" : "asc");

		return this.search({
				"q":    query,
				"page": page,
				"sf":   sortingMethod,
				"sd":   sortingOrder
			}, callback);
	},

	updateSuggestions: function() {
		this.$suggestionBox && this.$suggestionBox.addClass("updating");

		if(this.tags.length == 0) {
			//Display default suggestions
			this.suggestions = []
			for(var i = 0; i < this.systemTags.length; i++)
				this.suggestions.push({"tag": this.systemTags[i], "p": 2});
			this.displaySuggestions();
			return true;
		}

		var that = this;
		var imagesAnalyzed = 0;
		var currentPage = 1;
		var fuzzy = this.startFuzzy;
		var tagCounts = {};

		var analyzeImages = function(images) {
			if(that.config.DEBUG) {
				console.log("[TagSuggestions] Analyze images:");
				console.log(images);
			}

			for(var i = 0; i < images.length; i++) {
				var tags = images[i].tags.split(", ");
				for(var j = 0; j < tags.length; j++) {
					var tag = tags[j];
					if(!tagCounts[tag])
						tagCounts[tag] = 1;
					else
						tagCounts[tag]++;
				}
			}

			imagesAnalyzed += images.length;
		};

		var compileSuggestions = function() {
			if(that.config.DEBUG) {
				console.log("[TagSuggestions] Compile suggestions:");
				console.log(tagCounts);
			}

			that.suggestions = [];
			for (var tag in tagCounts)
				that.suggestions.push({"tag": tag, "p": tagCounts[tag] / imagesAnalyzed});
			that.suggestions.sort(function(a, b) { return b.p - a.p; });

			that.displaySuggestions();
		};

		var callback = function(data) {
			if(that.config.DEBUG) {
				console.log("[TagSuggestions] Got answer:");
				console.log(data);
			}

			if(!fuzzy && data.total < that.config.MIN_SAMPLE_SIZE) {
				//Repeat with fuzzy search
				if(that.config.DEBUG)
					console.log("[TagSuggestions] Switching to fuzzy search");
				that.startFuzzy = fuzzy = true;
				that.tagSearch(callback, currentPage, fuzzy);
				return;
			}

			analyzeImages(data.search);

			if(imagesAnalyzed < Math.min(data.total, that.config.SAMPLE_SIZE) && currentPage < that.config.SAMPLE_SIZE / 50) {
				//Next page
				that.tagSearch(callback, ++currentPage, fuzzy);
			} else {
				//Have enough images
				compileSuggestions();
			}
		};

		return this.tagSearch(callback, currentPage, fuzzy);
	},

	initSuggestions: function() {
		if(!this.generatedOnce) {
			this.generatedOnce = true;
			this.updateSuggestions();
		}
	},

	displaySuggestions: function() {
		if(this.config.DEBUG) {
			console.log("[TagSuggestions] New suggestions:");
			console.log(this.suggestions);
		}

		if(!this.$suggestionBox) return;

		this.$suggestionBox.html("&nbsp;Suggested Tags: ");
		var validSuggestions = false;

		for(var i = 0, j = 0; i < this.suggestions.length && j < this.config.MAX_SUGGESTIONS
				&& this.suggestions[i].p >= this.config.MIN_RELEVANCE; i++) {
			var tag = this.suggestions[i].tag;

			if(this.tags.indexOf(tag) >= 0
				|| this.ignoreTags.indexOf(tag) >= 0
				|| this.excludeTags.indexOf(tag) >= 0)
				continue; //Don't show already applied, ignored or excluded tags

			j++;
			validSuggestions = true;

			var p   = Math.round(this.suggestions[i].p * 100, 0);
			var fading = (p == 200) ? 0 : (1 - (Math.min(50, p) / 50));
			var that = this;

			var $button = $('<span class="tag" style="filter:grayscale(' + fading + ')"></span>');
			if(this.systemTags.indexOf(tag) >= 0)
				$button.addClass("tag-system");
			var namespaceLength = tag.indexOf(":");
			if(namespaceLength >= 0)
				$button.addClass("tag-ns-" + tag.substring(0, namespaceLength));
			$button.data("tag-slug", tag.replace(" ", "+")); //Not entirely accurate, but enough for colored tags

			var $tag = $('<span>' + this.suggestions[i].tag + (p != 200 ? ' (' + p + '%)' : '') + '</span>')
			.on('click', function(tag) { return function() { that.addTag(tag); } }(tag));
			$button.append($tag);

			var $exclude = $(' <a class="exclude">&nbspx</a>')
			.on('click', function(tag) { return function() { that.excludeTag(tag); } }(tag));
			$button.append($exclude);

			this.$suggestionBox.append($button);
		}

		if(!validSuggestions)
			this.$suggestionBox.append('<i>No suggested tags</i>');

		this.$suggestionBox.removeClass("updating");
	},

	addTag: function(tag) {
		window.addTag(this.tagsinput, tag, this.tagscontainer);
	},

	excludeTag: function(tag) {
		this.excludeTags[this.excludeTags.length] = tag;
		this.updateSuggestions();
	},

	initTags: function(tags) {
		for(var i = 0; i < tags.length; i++) {
			var tag = tags[i].trim();
			if(tag != "") this.tags[this.tags.length] = tag;
		}
		if(this.config.DEBUG)
			console.log("[TagSuggestions] Tags were intialized: " + this.tags.join(","));
	},

	updateTags: function(that) {
		var tags = that.tagsinput.value.split(",");
		var newTags = [];
		for(var i = 0; i < tags.length; i++) {
			var tag = tags[i].trim();
			if(tag != "") newTags[newTags.length] = tag;
		}

		if(newTags.length == that.tags.length) {
			var match = true;
			for(var i = 0; i < newTags.length; i++) {
				if(newTags[i] != that.tags[i]) {
					match = false;
					break;
				}
			}
			if(match) {
				if(that.config.DEBUG)
					console.log("[TagSuggestions] Tags were unchanged");
				return;
			}
		}

		that.tags = newTags;
		that.startFuzzy = false;

		if(that.config.DEBUG)
			console.log("[TagSuggestions] Tags were updated: " + this.tags.join(","));

		that.updateSuggestions();
	},

	tagAdded: function(tag) {
		tag = tag.trim();
		this.tags[this.tags.length] = tag;
		if(this.config.DEBUG)
			console.log("[TagSuggestions] Tag was added: " + tag);

		this.updateSuggestions();
	},

	tagRemoved: function(tag) {
		tag = tag.trim();
		this.tags.splice(this.tags.indexOf(tag), 1);
		this.startFuzzy = false;
		if(this.config.DEBUG)
			console.log("[TagSuggestions] Tag was removed: " + tag);

		this.updateSuggestions();
	},

	run: function(config) {
		this.config = config;

		/* Add styles */
		$("head").append('<style type="text/css">' + this.styles.replace("%MAX_HEIGHT%", this.config.MAX_DISPLAY_ROWS * 35) + '</style>');

		/* Setup DOM/events */
        if (document.getElementsByClassName('fancy-tag-edit').length > 0) {
            this.tagscontainer = document.getElementsByClassName('fancy-tag-edit')[0].parentNode;
        }
		this.tagsinput = document.querySelector('textarea.tagsinput');
		if(!this.tagsinput) {
			console.log("[TagSuggestions] No tag input field found. Nothing to do here.");
			return;
		}
		this.initTags(this.tagsinput.value.split(","));
		//Setup Observer (Chrome 18+, Firefox 14+, IE 11+, Safari 6+)
		var that = this;
		var setupObserver = function() {
			var tagsinputdiv = document.querySelector('div.tagsinput');
			if(tagsinputdiv) {
				//Reinit tags on bulk changes
				$(that.tagsinput).parent().find("button").on("click", function() {
					that.updateTags(that);
				});

				//Initial update happens when clicking on edit, or immeadiately on a new upload
				var editButton = document.querySelector("#tag-sauce-toggle");
				if(editButton)
					editButton.addEventListener("click", function() { that.initSuggestions(); });
				else
					that.initSuggestions();

				//Init observer
				that.tagsinputdiv = tagsinputdiv;
				var observer = new MutationObserver(function(mutations) {
					//Toggling the editor removes every node and adds
					//it back, so we just ignore all bulk changes
					var mutation = mutations[0];
					if(mutations.length == 3 && mutation.addedNodes  .length == 1) {
						var addedSpan   = mutation.addedNodes[0]  .firstChild;
						if(addedSpan.tagName   == "SPAN") that.tagAdded  (addedSpan  .innerHTML);
					}
					if(mutations.length == 1 && mutation.removedNodes.length == 1) {
						var removedSpan = mutation.removedNodes[0].firstChild;
						if(removedSpan.tagName == "SPAN") that.tagRemoved(removedSpan.innerHTML);
					}
				});
				observer.observe(tagsinputdiv, { childList: true, attributes: true });
				if(that.config.DEBUG)
					console.log("[TagSuggestions] Initialized observer");
			} else {
				window.setTimeout(setupObserver, 100);
				if(that.config.DEBUG)
					console.log("[TagSuggestions] Couldn't find tagsinputdiv, retrying...");
			}
		};
		window.setTimeout(setupObserver, 100);

		//Add suggestion box
		this.$suggestionBox = $('<div class="suggested_tags">&nbsp;Suggested tags: <i>Loading...</i></div>');
		$(this.tagsinput).parent().append(this.$suggestionBox);

		/* Finished init */
		if(this.config.DEBUG)
			console.log("[TagSuggestions] Init complete");
	}
}; //window.tagSuggestions

$(function() {
	window.tagSuggestions.run(config);
});